package com.syntax.class03;

public class DoubleComparison {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*Create a Java program and name it Double Comparison. Declare 2 double values and if value of 
		 * first variable is higher then the second, print “Double value  is larger than  .” Otherwise print...
		 */
double number1=3.25;
double number2=1.25;
if(number1>number2) {
	System.out.println("Double value "+number1+" is larger than"+number2);
} else {
	System.out.println("Double value "+number2+" is larger than "+number1);
}
	}

}
