package com.syntax.class03;

public class RecapOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/*
 * Addition ->+
 * subtraction ->-
 * mult->*
 * div-> /
 * Modulus->%
 * 
 * 
 */
		int number1=9;
			int number2=2;
			System.out.println("Answer = "+number1/number2);
			System.out.println("Remainder= " + number1%number2);
			System.out.println(number1/number2); //dividing number1 by number2
			
			double number3=9;
			double number4=2;
			System.out.println(number3/number4); //dividing number3 by number4
			
			
	}

	}


