package com.syntax.class08;

public class Patterns {

	public static void main(String[] args) {
		// TODO Auto-generated method
		for(int i=1; i<=3; i++) {
			System.out.print("* ");
		}
		System.out.println();
		for(int i=1; i<=5; i++) {
			System.out.print("* ");
		}
		System.out.println("---------------------");
		System.out.println();
		for(int i=1; i<=5; i++) {
			for(int j=1; j<=5; j++) {
				System.out.print("* ");
			}
			System.out.println();
		}
		System.out.println("-------------");
		
	}

}
