package com.syntax.reviewClass4;

import java.util.Scanner;

public class ScannerLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


for (int j=0; j<2; j++) {
	for(int i=0; i<3; i++) {
		System.out.println(i);
}
	}
System.out.println("--------------------------");
for (int j=0; j<5; j++) {
	int i=0;
	while(i<3) {
		System.out.println(i);
		i++;
	}
}
}}
